import React from "react";
import styled from "styled-components/macro";
import NetflixLogo from "../../logo.svg";

export default function SignUn() {
  return (
    <div className="header-container">
      <SignUpHeaderContainer>
        <SignUpHeaderLogo to="/" src={NetflixLogo} alt="Netflix" />
        <SignUpHeaderButtonLink to="/signup">Signup</SignUpHeaderButtonLink>
      </SignUpHeaderContainer>

      <SUForm>
        <SUTitle>Sign Up</SUTitle>
        <SUBase>
          <SUInput placeholder="First name" />
          <SUInput placeholder="Email address" />
          <SUInput type="password" autoComplete="off" placeholder="Password" />
          <SUSubmit type="submit" data-testid="sign-up">
            Sign Up
          </SUSubmit>
        </SUBase>

        <SUText>
            Already a user? <SULink to="/signin">Sign in now.</SULink>
          </SUText>
          <SUTextSmall>
            This page is protected by Google reCAPTCHA to ensure you're not a bot. Learn more.
          </SUTextSmall>
      </SUForm>
    </div>
  );
}

export const SignUpHeaderButtonLink = styled.button`
  display: block;
  background-color: #e50914;
  width: 90px;
  height: fit-content;
  color: white;
  border: 0;
  font-size: 15px;
  border-radius: 3px;
  padding: 8px 17px;
  cursor: pointer;
  text-decoration: none;

  &:hover {
    background: #f40612;
  }
`;

export const SignUpHeaderLogo = styled.img`
  height: 36px;
  width: 134px;
  margin-right: 40px;

  @media (min-width: 1449px) {
    height: 45px;
    width: 167px;
  }
`;

export const SignUpHeaderContainer = styled.div`
  display: flex;
  margin: 0 56px;
  height: 100px;
  justify-content: space-between;
  align-items: center;

  a {
    display: flex;
  }

  @media (max-width: 1000px) {
    margin: 0 30px;
  }
`;

const SUForm = styled.div`
  display: flex;
  flex-direction: column;
  min-height: 660px;
  background-color: rgba(0, 0, 0, 0.75);
  border-radius: 5px;
  width: 100%;
  margin: auto;
  max-width: 450px;
  padding: 60px 68px 40px;
  margin-bottom: 100px;
`;

const SUBase = styled.form`
  display: flex;
  flex-direction: column;
  max-width: 450px;
  width: 100%;
`;

const SUTitle = styled.h1`
  color: #fff;
  font-size: 32px;
  font-weight: bold;
  margin-bottom: 28px;
`;

const SUText = styled.p`
  color: #737373;
  font-size: 16px;
  font-weight: 500;
`;

const SUTextSmall = styled.p`
  margin-top: 10px;
  font-size: 13px;
  line-height: normal;
  color: #8c8c8c;
`;

const SUInput = styled.input`
  background: #333;
  border-radius: 4px;
  border: 0;
  color: #fff;
  height: 50px;
  line-height: 50px;
  padding: 5px 20px;
  margin-bottom: 20px;

  &:last-of-type {
    margin-bottom: 30px;
  }
`;

const SULink = styled.a`
  color: #fff;
  text-decoration: none;

  &:hover {
    text-decoration: underline;
  }
`;

const SUSubmit = styled.button`
  background: #e50914;
  border-radius: 4px;
  font-size: 16px;
  font-weight: bold;
  margin: 24px 0 12px;
  padding: 16px;
  border: 0;
  color: white;
  cursor: pointer;

  &:disabled {
    opacity: 0.5;
  }
`;
